import{_ as e,c as a,o as t,a as r}from"./app.b7c4ed3a.js";const f='{"title":"Fertile Crescent","description":"","frontmatter":{"index":false,"hide":false,"search":true,"sidebar":true,"aliases":["Fertile Crescent"],"tags":null,"date_created":"2021-12-28T09:40:50.000Z","date_modified":"2021-12-28T09:51:08.000Z"},"headers":[{"level":2,"title":"1. the term","slug":"_1-the-term"},{"level":3,"title":"1.1. geopolitical","slug":"_1-1-geopolitical"},{"level":3,"title":"1.2. ecological","slug":"_1-2-ecological"}],"relativePath":"fertile_crescent.md","lastUpdated":1640822312199}',i={},n=r('<h1 id="fertile-crescent" tabindex="-1">Fertile Crescent <a class="header-anchor" href="#fertile-crescent" aria-hidden="true">#</a></h1><h2 id="_1-the-term" tabindex="-1">1. the term <a class="header-anchor" href="#_1-the-term" aria-hidden="true">#</a></h2><h3 id="_1-1-geopolitical" tabindex="-1">1.1. geopolitical <a class="header-anchor" href="#_1-1-geopolitical" aria-hidden="true">#</a></h3><ul><li>term &quot;Fertile Crescent&quot; invented in 19th century (when European imperial powers partitioning it according to their strategic interests)</li><li>widely adopted among researchers to mean: <ul><li>area from eastern shores of Mediterranean to the foothills of the [[Zagros Mountains]], crossing parts of Syria, Turkey, and Iraq</li></ul></li><li>only prehistorians use it now, to indicate the region where farming began</li></ul><h3 id="_1-2-ecological" tabindex="-1">1.2. ecological <a class="header-anchor" href="#_1-2-ecological" aria-hidden="true">#</a></h3><ul><li>really multiple crescents</li><li>around 10,000 bc (end of last glacial period) area went in two distinct directions, an upland and a lowland crescent</li></ul><h4 id="_1-2-1-upland-crescent" tabindex="-1">1.2.1. upland crescent <a class="header-anchor" href="#_1-2-1-upland-crescent" aria-hidden="true">#</a></h4><blockquote><p>follows the foothills of the Taurus and Zagros Mountains, running north of the modern border between Syria and Turkey. For foragers at the end of the last Ice Age, it would have been something of an open frontier; an expanding belt of oak-pistachio forest and game-rich prairie intersected by river valleys.</p></blockquote><h4 id="_1-2-2-lowland-crescent" tabindex="-1">1.2.2. lowland crescent <a class="header-anchor" href="#_1-2-2-lowland-crescent" aria-hidden="true">#</a></h4><blockquote><p>characterized by <em>Pistacia</em> woodlands, as well as tracts of fertile terrain bound tightly to river systems or to the shores of lakes and artesian springs, beyond which lay deserts and barren plateaus.[^1]</p></blockquote><p>[^1]: <a href="./dawn_of_everything_graeber_wengrow.html">Dawn of everything</a> p 226</p>',11),o=[n];function l(s,c,d,h,u,_){return t(),a("div",null,o)}var g=e(i,[["render",l]]);export{f as __pageData,g as default};
