import{_ as s,c as a,o,b as e,d as t}from"./app.b7c4ed3a.js";const v='{"title":"pre-servile institutions","description":"","frontmatter":{"index":false,"hide":false,"search":true,"sidebar":true,"aliases":null,"tags":null,"date_created":"2021-12-28T12:59:20.000Z","date_modified":"2021-12-28T12:59:22.000Z"},"relativePath":"pre-servile_institutions.md","lastUpdated":1640822312463}',r={},i=e("h1",{id:"pre-servile-institutions",tabindex:"-1"},[t("pre-servile institutions "),e("a",{class:"header-anchor",href:"#pre-servile-institutions","aria-hidden":"true"},"#")],-1),n=e("blockquote",null,[e("p",null,"The shorter version of Steiner\u2019s doctoral work, which does survive, focuses on what he calls \u2018pre-servile institutions\u2019. Poignantly, given his own life story, it is a study of what happens in different cultural and historical situations to people who become unmoored: those expelled from their clans for some debt or fault; castaways, criminals, runaways. It can be read as a history of how refugees such as himself were first welcomed, treated as almost sacred beings, then gradually degraded and exploited, again much like the women working in the Sumerian temple factories. In essence, the story told by Steiner appears to be precisely about the collapse of what we would term the first basic freedom (to move away or relocate), and how this paved the way for the loss of the second (the freedom to disobey).[^1]")],-1),l=e("p",null,[t("[^1]: "),e("a",{href:"./dawn_of_everything_graeber_wengrow.html"},"Dawn of everything"),t(" p 519")],-1),d=[i,n,l];function c(h,u,p,f,_,m){return o(),a("div",null,d)}var b=s(r,[["render",c]]);export{v as __pageData,b as default};
